CREATE TABLE IF NOT EXISTS user (id SERIAL PRIMARY KEY, username VARCHAR(32), password VARCHAR(255), email VARCHAR(255), admin boolean, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS invite (id SERIAL PRIMARY KEY, code VARCHAR(128), user_id BIGINT, admin boolean, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS session (id SERIAL PRIMARY KEY, token VARCHAR(128), user_id BIGINT, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, last_login TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS news (id SERIAL PRIMARY KEY, title VARCHAR(128), content VARCHAR(255), thumbnail VARCHAR(128));