CREATE TABLE user (id SERIAL PRIMARY KEY, username VARCHAR(32), password VARCHAR(255), email VARCHAR(255), admin boolean, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);
CREATE TABLE invite (code VARCHAR(128) PRIMARY KEY, userId BIGINT, admin boolean, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP);
CREATE TABLE session (token VARCHAR(128) PRIMARY KEY, userId BIGINT, created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP, last_login TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP);